sp_widget.client_script:
api.controller=function($rootScope, $scope) {
  /* widget controller */
  var c = this;
  c.selectItem = function(idx) {
    var id = c.data.notes[idx].sys_id;
    console.log('Note ID: ' + id);
		console.log("$index is: " + idx);
    $rootScope.noteID = id;
    $rootScope.$emit('selectNote', id);
  }

	$rootScope.$on('updateNoteList', function(event) {

		console.log("Response from notes BOdy is: " + $rootScope.body + " : " +$rootScope.title);
		c.server.update();
	})
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];



	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user', gs.getUser().getID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	while(gr.next()) {

		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, gr, 'number,title,sys_id');
		noteObj.notes = gr.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}


})();
